sys_script_include.script:
function validateEmailAddress(emailStr){
	//The ScriptInclude is reusable logic for BusinessRule which only has to be written once and can then be used indefinetily
	//Coercion to guaranee emailStr is a string
	emailStr = emailStr + '';
	//Compare emailStr against allowd syntax as specified in RegEx
	//If emailStr has allowed syntax, return true, else false
	if(emailStr.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)){
		return true;
	} else {
		return false;
	}
}
